mapper.select:
SELECT member_id, password, phone, email, reg_date, recent_date, status, fail_count, name
FROM tbl_member
WHERE member_id = #{member_id}

SELECT member_id, password, phone, email, reg_date, recent_date, status, fail_count, name
FROM tbl_member
WHERE member_id = #{member_id}

mapper.update:
UPDATE tbl_member
SET fail_count = fail_count + 1
where member_id = #{member_id}

UPDATE tbl_member
SET fail_count = 0
where member_id = #{member_id}

mapper.select:
SELECT count(*)
FROM tbl_member
WHERE member_id = #{member_id}

mapper.update:
UPDATE tbl_member
SET status = 'N'
WHERE member_id = #{member_id}

